import React, { useEffect, useState } from "react";
import axios from "axios";
import { useForm, Controller } from "react-hook-form";
import MenuHeader from "../../../components/auth/MenuHeader";

import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import PollIcon from "@mui/icons-material/Poll";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { Link } from "react-router-dom";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const theme = createTheme();

export default function IndexPoll() {

        const [polls, setPolls] = useState([]);
      
        useEffect(() => {
          displayPolls();
        }, []); // Sans les crochets ça tourne en boucle
      
        const displayPolls = async () => {
          await axios.get("http://localhost:8000/api/polls").then((res) => {
            setPolls(res.data);
            console.log(res.data);
          });
        };
        
        const deletePoll = (id) => {
          axios
            .delete(`http://localhost:8000/api/polls/${id}`)
            .then(displayPolls);
        };

  return (
    <div>
      <MenuHeader />

      <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 18,
              display: "flex",
              justifyContent: "center",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
    

            <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
              <PollIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Liste des sondages
            </Typography>
            <Box sx={{ mt: 1 }}>
              

            <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ID</TableCell>
            <TableCell align="center">Nom</TableCell>
            <TableCell align="center">Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {polls.map((poll) => (
            <TableRow
              key={poll.nameSondage}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {poll.id}
              </TableCell>
              <TableCell align="center">{poll.nameSondage}</TableCell>
              <TableCell align="center">


              <IconButton aria-label="delete" size="large">
  <DeleteIcon fontSize="inherit" />
</IconButton>


              <Link
                    to={`/dashboard/polls/edit/${poll.id}`}
                    className="btn btn-success me-2"
                  >
                    Modifier
                  </Link>
                  <Button
                    variant="danger"
                    onClick={() => {
                      deletePoll(poll.id);
                    }}
                  >
                    Supprimer
                  </Button>








              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

            </Box>
          </Box>
        </Container>
      </ThemeProvider>
    </div>
  );
}
