import React, { useState, useEffect  } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useForm, Controller } from "react-hook-form";
import MenuHeader from "../../../components/auth/MenuHeader";

import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import PollIcon from "@mui/icons-material/Poll";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const theme = createTheme();

export default function ShowPoll() {
  const { poll } = useParams();
  const { question } = useParams();
  const navigate = useNavigate();

  const [nameSondage, setNameSondage] = useState("");
  const [nameQuestion, setNameQuestion] = useState("");
  const [polls_id, setPolls_id] = useState("");
  const [polls, setPolls] = useState([]);
  const [questions_id, setQuestions_id] = useState("");
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    getPoll();
    getQuestion();
  }, []);

  // GET - Récupère les valeurs de la fiche avec l'API
  const getPoll = async () => {
    await axios
      .get(`http://localhost:8000/api/polls/${poll}`)
      .then((res) => {
        setNameSondage(res.data.nameSondage);
      })
      .catch((error) => {
        console.log(error);
      });
  };

    // GET - Récupère les valeurs de la fiche avec l'API
    const getQuestion = async () => {
      await axios
        .get(`http://localhost:8000/api/questions/${question}`)
        .then((response) => {
          setNameQuestion(res.data.nameQuestion);
        })
        .catch((error) => {
          console.log(error);
        });
        console.log(response.data);
    };
    
  return (
    <div>
      <MenuHeader />

      <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 18,
              display: "flex",
              justifyContent: "center",
              flexDirection: "column",
              alignItems: "center",
            }}
          >

            <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
              <PollIcon />
            </Avatar>
            <Typography sx={{ mt: 0 }} component="h1" variant="h4">
              Fiche de la question
            </Typography>
            <Box className="boxPoll">
           <p>{nameSondage}aaaaaaaaaa</p><br />
           <p>{nameQuestion}dddddddddd</p>
           </Box>
          </Box>
        </Container>
      </ThemeProvider>
    </div>
  );
}
